import React, { useState, useEffect } from "react";
import { Line } from "react-chartjs-2";
import axios from "./axios";
import numeral from "numeral";

const options = {
  legend: {
    display: false,
  },
  elements: {
    point: {
      radius: 4,
    },
  },
  maintainAspectRatio: false,
  tooltips: {
    mode: "index",
    intersect: false,
    callbacks: {
      label: function (tooltipItem, data) {
        console.log(tooltipItem);
        console.log(data);
        tooltipItem.label = "hi";
        return tooltipItem.label;
      },
    },
  },

  scales: {
    xAxes: [
      {
        type: "time",
        time: {
          //   format: "MM/DD/YY",
          //   tooltipFormat: "ll",
        },
      },
    ],
    yAxes: [
      {
        gridLines: {
          display: true,
        },
        ticks: {
          callback: function (value, index, values) {
            return value;
          },
        },
      },
    ],
  },
};

function LineGraph({ handle }) {
  const [UserRating, setUserRating] = useState({});

  const buildChartData = (data) => {
    const chartData = [];
    let lastDataPoint;
    data.forEach((rating) => {
      if (lastDataPoint) {
        const newDatapoint = {
          x: rating.ratingUpdateTimeSeconds / 31536000,
          y: Math.abs(rating.newRating),
        };
        chartData.push(newDatapoint);
      }
      lastDataPoint = rating.newRating;
    });
    return chartData;
  };

  useEffect(() => {
    const fetchData = async () => {
      const req = await axios.get(`/explore/Rating/${handle}`); // axios returns a promise
      const chartData = buildChartData(req.data);
      setUserRating(chartData);
    };
    fetchData();
  }, []);

  return (
    <div style={{ width: "80%", height: "350px" }}>
      {UserRating?.length > 0 && (
        <Line
          data={{
            datasets: [
              {
                // backgroundColor: "rgba(204, 16, 52, 0.8)",
                borderColor: "#CC1034",
                borderJoinStyle: "round",
                borderWidth: 2,
                data: UserRating,
                pointBackgroundColor: "yellow",
                pointHoverRadius: 5,
              },
            ],
          }}
          options={options}
        />
      )}
    </div>
  );
}

export default LineGraph;
