import React, { useEffect, useState } from "react";
import Card from "@material-ui/core/Card";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import "./TopProg.css";
import { Typography, CardMedia } from "@material-ui/core";
import axios from "./axios";
import topProgs from "./data";

function TopProg() {
  const [prog, setprog] = useState({});

  const time = new Date().getTime();
  const key = "ce72ebe1d56ebe106c52288686ac4c30c4539f07";
  const SecKey = "e4b23d29ca62a1d0adcf1c234120681bc2bd6941";

  async function fetchData(person) {
    const url =
      "https://codeforces.com/api/user.info?handles=" +
      person.handle +
      "&&apikey=" +
      key +
      "&time=" +
      time +
      "&apiSig=123456" +
      "sha512Hex(123456/user.info?handles=" +
      person.handle +
      "&apikey=" +
      key +
      "&time=" +
      time +
      "#" +
      SecKey +
      ")";

    const res = await fetch(url);
    const data = await res.json();
    setprog({
      image: data.result[0].titlePhoto,
      name: {
        fname: data.result[0].firstName,
        lname: data.result[0].lastName,
      },
    });
    console.log(prog);
  }

  return (
    <div className="topProgs">
      <h1>Top Competitive Programmers around the globe!</h1>
      <div className="topProgs_cards">
        {topProgs.map((person) => {
          fetchData(person);
          return (
            <Card>
              <CardContent>
                <CardMedia
                  style={{
                    height: "110px",
                    width: "220px",
                    paddingTop: "56.25%",
                  }}
                  image={prog.image}
                />
                <Typography color="textPrimary">
                  {prog.name.fname} {""} {prog.name.lname}
                </Typography>
              </CardContent>
            </Card>
          );
        })}
      </div>
    </div>
  );
}

export default TopProg;

