import React, { useEffect, useState } from "react";
import axios from "./axios";
import "./SingleUser.css";

function SingleUser() {
  const [UserData, setUserData] = useState({});

  useEffect(() => {
    const urlArray = window.location.href.split("/");
    const Handle = urlArray[urlArray.length - 1];
    async function getData() {
      const req = await axios.get(`/explore/${Handle}`); // axios returns a promise
      setUserData(req.data);
    }
    getData();
  }, []);

  return (
    <div className="single">
      <div className="single_info">
        <h1>
          {Data.firstName} {Data.lastName}
        </h1>
        <img src={Data.titlePhoto} alt={Data.Handle} />
        <h3>Current Rating: {Data.rating}</h3>
        <h3>Maximum Rating: {Data.maxRating}</h3>
        <h3>Title: {Data.rank}</h3>
      </div>
      <div className="single_graph">
        <h1>i am a graph</h1>
      </div>
    </div>
  );
}

export default SingleUser;
